import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Nav from 'react-bootstrap/Nav';
import { useEffect, useState } from "react";
import Create from '../../components/CRUD/create'
import axios from 'axios';



export function Cadastro(){

    const [pagina,setPagina] = useState(1)
    const [cep, setCep] = useState('');
    const [numero, setNumero] = useState('');
    const [nome , setNome] = useState('');
    const [idEndereco, setIdEndereco ] = useState();
    const [email, setEmail] = useState('');
    const [telefone, setTelefone] = useState('');
    const [descricao, setDescricao] = useState('');
    const [qntEstoque, setQntEstoque] = useState();
    const [valorUnitario, setValorUnitario,] = useState();
    const [imagemUrl, setImagemUrl,] = useState('');
    const [idCategoria, setIdCategoria] = useState( );


    const handleClick = (e) => {
        setPagina(e)
    }

    const postEndereco = () => {
        axios.post(`http://localhost:8080/endereco/save/cep`, {
            cep,
            numero
        })
    }

    const postCliente = () => {
        axios.post(`http://localhost:8080/cliente`, {
            nome,
            idEndereco,
            email,
            telefone
        })
    }
    const postProduto = () => {
        axios.post(`http://localhost:8080/produto`, {
            nome,
            descricao,
            qntEstoque,
            valorUnitario,
            imagemUrl,
            idCategoria
        })
    }

    const postCategoria = () => {
        axios.post(`http://localhost:8080/categoria`, {
            nome,
            descricao
        })
    }

    useEffect(() => {
        exibirPagina()
      });

    const exibirPagina = () => {
        if (pagina===1){
            return (

            <Card.Body>
            <Card.Title>Cadastro de Endereço</Card.Title>
                <Card.Text>
                <div>

                <label>CEP: </label>
                <br/>
                <input onChange={(e) => setCep(e.target.value)}/>

                <br/>
                <label>Numero: </label>
                <br/>
                <input onChange={(e) => setNumero(e.target.value)}/>

            </div>
            </Card.Text>
            <Button onClick={postEndereco} type='submit'>Submit</Button>
            </Card.Body>
            )
        }
        if (pagina===2){
            return (
                <Card.Body>
            <Card.Title>Cadastro de Cliente</Card.Title>
                <Card.Text>
                <div>

                <label>Nome: </label>
                <br/>
                <input onChange={(e) => setNome(e.target.value)}/>

                <br/>
                <label>IdEndereco: </label>
                <br/>
                <input onChange={(e) => setIdEndereco(e.target.value)}/>

                <br/>
                <label>Email: </label>
                <br/>
                <input onChange={(e) => setEmail(e.target.value)}/>

                <br/>
                <label>Telefone: </label>
                <br/>
                <input onChange={(e) => setTelefone(e.target.value)}/>
            </div>
            </Card.Text>
            <Button onClick={postCliente} type='submit'>Submit</Button>
            </Card.Body>

            )
        }
        if (pagina===4){
            return (
                <Card.Body>
            <Card.Title>Cadastro de Produto</Card.Title>
                <Card.Text>
                <div>

                <label>Nome: </label>
                <br/>
                <input onChange={(e) => setNome(e.target.value)}/>

                <br/>
                <label>Descrição: </label>
                <br/>
                <input onChange={(e) => setDescricao(e.target.value)}/>

                <br/>
                <label>Qnt Estoque: </label>
                <br/>
                <input onChange={(e) => setQntEstoque(e.target.value)}/>

                <br/>
                <label>Valor Unitario: </label>
